import React, { Component } from 'react'
import _ from 'lodash';
import history from './history'
import AddUser from './Redux-storage/action';
let array=[];
export default class List extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: '',
      items: []
    }
  }

  onInputChange(e) {
    this.setState({
      inputValue: e.target.value,
    });
  }

  addItem() {
    let items = this.state.items;
    items.push(this.state.inputValue);

    this.setState({
      items
    });
  }
  delItem()
  { 
    let items = this.state.items
    let update=[]
    //   eslint-disable-next-line
    update=_.pullAt(items,array);
    update.pop()
    array=[];
    this.setState({
      items
    });
  }
  handlechange(e,id)
  {
    array.push(id)
  }

  listItems() {
    let items = this.state.items;
    return (
      <ol>
        {

          items.map((val, id) => {
            return (
              <li key={id}>
                <input  type="checkbox" name="isDone"ref="check_me" checked={items.isDone} onChange={(e)=>this.handlechange(e,id)} />
                  {val}
              </li>
            );
          })
        }
      </ol>

    );
  }
handleLogout(){
    history.push('/')
}


    render() {
        return (
            <div>

                <h1>This is your Dashboard</h1>
                <input type="text" onChange={ (e) => this.onInputChange(e) } />
                <button onClick={ () => this.addItem() }>Add item</button>
                <button onClick={ () => this.delItem() }>delete item</button>
                <br/>
                { this.listItems() }
                <br/>
                <button onClick={(e=>this.handleLogout(e))}>logout</button>

            </div>
        )
    }
}
const mapStateToProps = state => {
  return {
    data: state.user
  };
};

const mapDispachToProps = dispatch => {
  return {
    onAgeUp: () => dispatch(AddUser)
  };
};
